DROP TABLE IF EXISTS `cpf_invest_request`;

CREATE TABLE `cpf_invest_request` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `trade_serial_no` varchar(64) DEFAULT NULL COMMENT '交易系统-申购申请流水号=id',
  `order_id` varchar(64) DEFAULT NULL COMMENT '订单系统-orderId',
  `member_id` varchar(64) DEFAULT NULL COMMENT '会员id:会员系统',
  `product_id` varchar(64) DEFAULT NULL COMMENT '产品id',
  `invest_amount` decimal(16,4) DEFAULT NULL COMMENT '购买金额',
  `app_sheet_no` varchar(64) DEFAULT NULL COMMENT '资产系统-申购申请流水号',
  `account_id` varchar(64) DEFAULT NULL COMMENT '账户id',
  `sales_channel_no` varchar(64) DEFAULT NULL COMMENT '11-唐小僧 12-企业理财 21-摇旺',
  `user_type` varchar(32) DEFAULT NULL COMMENT '1-个人 2-机构',
  `trd_time` datetime DEFAULT NULL COMMENT '发起交易的细时间',
  `trd_date` datetime DEFAULT NULL COMMENT '正式开始交易的日期',
